// lib/core/config.dartimport '../languages/en.dart';import '../languages/ja.dart';import '../languages/zh.dart';import '../languages/es.dart';import '../languages/ar.dart';import '../languages/fr.dart';import '../languages/hi.dart';import '../languages/id.dart';import '../languages/pt.dart';import '../languages/bn.dart';import '../languages/ru.dart';import '../languages/de.dart';import '../languages/ko.dart';import '../languages/vi.dart';import '../languages/not_language.dart';import '../languages/language_interface.dart';class Config { static List<Language> defaultLanguages = [  EnglishLanguage(),  JapaneseLanguage(),  ChineseLanguage(),  SpanishLanguage(),  ArabicLanguage(),  FrenchLanguage(),  HindiLanguage(),  IndonesianLanguage(),  PortugueseLanguage(),  BengaliLanguage(),  RussianLanguage(),  GermanLanguage(),  KoreanLanguage(),  VietnameseLanguage(),  NotLanguage(), ]; static List<Language> getLanguage(String code) {  return defaultLanguages.where((lang) => lang.code == code).toList(); }}