// lib/core/config.dartimport '../languages/language_interface.dart';/// LanguageRegistry は、登録された Language インスタンスのグローバルリストを管理class LanguageRegistry { // 登録された言語の内部リスト static final List<Language> _languages = []; /// Language インスタンスを登録 static void register(Language language) {  _languages.add(language); } /// 登録された言語のリスト（変更不可）を返す static List<Language> get languages => List.unmodifiable(_languages);}/// Config は、デフォルトの言語設定などのグローバル設定を提供class Config { /// 登録された全ての言語をデフォルトとして返す static List<Language> get defaultLanguages => LanguageRegistry.languages; /// 指定された言語コードに一致する言語リストを返す static List<Language> getLanguage(String code) {  return defaultLanguages.where((lang) => lang.code == code).toList(); }}